import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  Button,
  FormControl,
  FormLabel,
  Input,
  Checkbox,
} from "@chakra-ui/react";
import { useState, useEffect } from "react";
import axiosInstance from "./axiosInstances";

const JanelaModal = ({ data, setData, dataEdit, isOpen, onClose }) => {
  const [cpf, setCpf] = useState(dataEdit.cpf || "");
  const [name, setName] = useState(dataEdit.name || "");
  const [age, setAge] = useState(dataEdit.age || "");
  const [sintomasSelecionados, setSintomasSelecionados] = useState(
    dataEdit.sintomas || []
  );
  const listaSintomas = [
    "coceira",
    "dor_na_pele",
    "dor_no_corpo",
    "fadiga",
    "febre",
    "febre_alta",
    "inflamacao_na_pele",
    "palidez",
    "perda_de_peso",
    "chiado_no_peito",
    "falta_de_ar",
    "tosse_persistente",
    "tosse_seca",
    "dor_de_cabeca",
    "nausea",
    "sensibilidade_a_luz",
    "tontura",
    "visao_turva",
    "coceira_anal",
    "dor_ao_evacuar",
    "sangramento_ao_evacuar",
    "azia",
    "dor_no_estomago",
  ];

  // Renderiza as checkboxes com os labels

  const handleSintomasChange = (e) => {
    const sintomasSelecionadosTemp = [...sintomasSelecionados];
