  setSintomasSelecionados(sintomasSelecionadosTemp);
  };

  const handleSave = () => {
    if (!cpf || !name) return;

    const obj = {
      cpf: cpf,
      nome: name,
      idade: age,
      sintomas: sintomasSelecionados.join("|") + "|",
    };

    axiosInstance
      .post("/atualizar_paciente", obj)
      .then((resposta) => {
        console.log(resposta);
      })
      .catch((erro) => {
        console.error("Erro GET:", erro.message);
      });

    onClose();
  };

  return (
    <>
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Cadastro de Paciente</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <FormControl display="flex" flexDir="column" gap={4}>
              <FormLabel>CPF</FormLabel>
              <Input
                type="text"
                value={cpf}
                onChange={(e) => setCpf(e.target.value)}
              />

              <FormLabel>Nome</FormLabel>
              <Input
                type="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />

              <FormLabel>Idade</FormLabel>
              <Input
                type="number"
                value={age}
                onChange={(e) => setAge(e.target.value)}
              />

              <FormLabel>Sintomas</FormLabel>
              {/* Loop para renderizar uma checkbox para cada sintoma */}
              {sintomasSelecionados.map((sintoma, index) => (
                <Checkbox
                  key={index}
                  value={sintoma}
                  isChecked={parseInt(sintoma) === 1}
                  onChange={handleSintomasChange}
                >
                  {listaSintomas[index]}
                </Checkbox>
              ))}
            </FormControl>
          </ModalBody>

          <ModalFooter justifyContent="start">
            <Button colorScheme="green" mr={3} onClick={handleSave}>
              SALVAR
            </Button>
            <Button colorScheme="red" onClick={onClose}>
              CANCELAR
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </>
  );
};

export default JanelaModal;
