import { useState } from "react";
import { Link } from "react-router-dom";
import "../../../styles/style.css";

export const Body = () => {
  //Define um estado para armazenar os valores dos campos do formulário
  const [formValues, setFormValues] = useState({
    coceira: false,
    dor_na_pele: false,
    dor_no_corpo: false,
    fadiga: false,
    febre: false,
    febre_alta: false,
    inflamação_na_pele: false,
    palidez: false,
    perda_de_peso: false,
  });

  const handleInputChange = (event) => {
    const { name, checked } = event.target;
    setFormValues({ ...formValues, [name]: checked });
  };

  return (
    <div>
      <h1>Sintomas no Corpo</h1>
      <form>
        <label>
          <input
            type="checkbox"
            name="coceira"
            checked={formValues.coceira}
            onChange={handleInputChange}
          />
          coceira
        </label>
        <label>
          <input
            type="checkbox"
            name="dor_na_pele"
            checked={formValues.dor_na_pele}
            onChange={handleInputChange}
          />
          Falta de ar
        </label>
        <label>
          <input
            type="checkbox"
            name="tosse_persistente"
            checked={formValues.tosse_persistente}
            onChange={handleInputChange}
          />
          Tosse persistente
        </label>
        <label>
          <input
            type="checkbox"
            name="tosse_seca"
            checked={formValues.tosse_seca}
            onChange={handleInputChange}
          />
          Tosse seca
        </label>
        <Link to="/stomach">
          <button type="submit">Próximo</button>
        </Link>
      </form>
    </div>
  );
};
