import { useState } from "react";
import { Link } from "react-router-dom";
import { vales } from "../../"
import Button from "react-bootstrap/Button";
import FinalizarConsulta from "../../../components/FinalizarConsulta";

import "../../../styles/style.css";

export const Intimate = () => {
  //Define um estado para armazenar os valores dos campos do formulário
  const [formValues, setFormValues] = useState({
    coceira_anal: false,
    dor_ao_evacuar: false,
    sangramento_ao_evacuar: false,
  });

  // Função que atualiza o estado quando um campo é modificado
  const handleInputChange = (event) => {
    const { name, checked } = event.target;
    setFormValues({ ...formValues, [name]: checked });
  };

  // Função que será chamada ao clicar no botão "Finalizar consulta"
  const handleFinishConsult = () => {
    const data = {
      coceira_anal,
      dor_ao_evacuar,
      sangramento_ao_evacuar,
      formValues,
      // Importa as variáveis do estado do ClientContext
      cpf,
      name,
      age,
      coceira,
      dor_na_pele,
      dor_no_corpo,
      fadiga,
      febre,
      febre_alta,
      inflamacao_na_pele,
      palidez,
      perda_de_peso,
      chiado_no_peito,
      falta_de_ar,
      tosse_persistente,
      tosse_seca,
      dor_de_cabeca,
      nausea,
      sensibilidade_a_luz,
      tontura,
      visao_turva,
      azia,
      dor_no_estomago,
    };
    FinalizarConsulta(data);
  };

  return (
    <div class="main-div">
      <div class="box-div">
        <h1>Sintomas nas Regiões Íntimas</h1>
        <form>
          <label>
            <input
              type="checkbox"
              name="coceira_anal"
              checked={formValues.coceira_anal}
              onChange={handleInputChange}
            />
            <span>&nbsp;</span>Coceira anal
          </label>
          <label>
            <input
              type="checkbox"
              name="dor_ao_evacuar"
              checked={formValues.dor_ao_evacuar}
              onChange={handleInputChange}
            />
            <span>&nbsp;</span>Dor ao evacuar
          </label>
          <label>
            <input
              type="checkbox"
              name="sangramento_ao_evacuar"
              checked={formValues.sangramento_ao_evacuar}
              onChange={handleInputChange}
            />
            <span>&nbsp;</span>Sangramento ao evacuar
          </label>
          <Button
            type="button"
            className="btn-custom"
            onClick={handleFinishConsult}
          >
            Finalizar consulta
          </Button>
          <Link to="/result">Resultado</Link>
        </form>
      </div>
    </div>
  );
};
