import { Button } from "@chakra-ui/react";
import SintomasModal from "../components/SintomasModal";
import { useDisclosure } from "@chakra-ui/react";
import React, { useState, useEffect } from "react";
import axiosInstance from "../components/axiosInstances";

import "../styles/style.css";

export default function Doenca(props) {
  const [sintomas, setSintomas] = useState([]);

  useEffect(() => {
    const url = "/listar_sintomas";
    axiosInstance
      .post(url, objeto)
      .then((resposta) => {
       setSintomas(resposta);
       console.log("Resposta retornada: ",resposta);
      })
      .catch((erro) => {
        console.error("Erro ao enviar objeto:", erro.message);
      });
  }, []);

  const { isOpen, onOpen, onClose } = useDisclosure();
  return (
    <div className="doenca-div">
      <p>
        {props.numero}
        {props.nome}
        <span>&nbsp;</span>
        <span>&nbsp;</span>
        <span>&nbsp;</span>
        {props.chance}%<span>&nbsp;</span>
        <span>&nbsp;</span>
        <span>&nbsp;</span>
        <Button
          colorScheme="orange"
          variant="outline"
          size="xs"
          onClick={() => [onOpen()]}
        >
          Ver mais
        </Button>
        {isOpen && <SintomasModal isOpen={isOpen} onClose={onClose} />}
      </p>
    </div>
  );
}
